import React, {useState, useEffect} from "react";
import { Link } from "react-router-dom";

import useDocumentTitle from "../useDocumentTitle";

import "./pages.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBackwardStep, faCaretLeft, faCaretRight, faForwardStep, faHeart, faMoon, faPause, faPlay, faScaleBalanced, faSun, faUser } from "@fortawesome/free-solid-svg-icons";

function Pages(props) {
  const [no, setNo] = useState(false);
  const [yes, setYes] = useState(false);

  useDocumentTitle(`${props.title}`);

  function clickButton(id) {
    var button = document.getElementById(id);
    button.classList.add("onclick");
    setTimeout(function() {
      button.classList.remove("onclick");
    }, 190);
  }

  function hidePopup() {
    var button = document.getElementById("pop");
    button.classList.add("hide");
    setTimeout(function() {
      button.classList.remove("hide");
    }, 190);
  }

  return (
    <div className="container">

      <div className="section">
        <div className="gif">
          <img src={require('../assets/images/cat.gif')} alt="gif" />
        </div>
        <div className="message">
          {
            (yes === false) ? 'Will you be my Valentine?' : 'Thank You!!!'
          }
        </div>
        <div className="buttons">
          <button className="bt-primary" id="yes-button" onClick={() => {clickButton("yes-button")}}>Yes</button>
          <button className="bt-secondary" id="no-button" onClick={() => { setTimeout(function() {setNo(true);}, 200); clickButton("no-button")}}>No</button>
        </div>
      </div>

      <div id="pop" className={`pop-up ${no === true && `show`}`}>
        <div className="pop-up-container">
          <div className="pop-up-info">
            <div className="pop-up-title">
              Error
            </div>
            <div className="pop-up-message">
              Oops, something went wrong. Please try<br/>again later.
            </div>
          </div>
          <button className="pop-up-button bt-primary" id="pop-up-button" onClick={() => {setTimeout(function() {setNo(false);}, 200); hidePopup(); clickButton("pop-up-button")}}>
            OK
          </button>
        </div>
      </div>

      <div class="hero_area">
          <svg class="waves" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" viewBox="0 24 150 28" preserveAspectRatio="none" shape-rendering="auto">
              <defs>
                  <path id="gentle-wave" d="M-160 44c30 0 58-18 88-18s 58 18 88 18 58-18 88-18 58 18 88 18 v44h-352z" />
              </defs>
              <g class="parallax">
                  <use xlinkHref="#gentle-wave" x="48" y="0" fill="#DFABB4" />
                  <use xlinkHref="#gentle-wave" x="48" y="6" fill="#CD707A" />
                  <use xlinkHref="#gentle-wave" x="48" y="12" fill="#CC3344" />
              </g>
          </svg>
      </div>
    </div>
  );
}

export default Pages;
