import React, {useState, useEffect} from "react";

import useDocumentTitle from "../useDocumentTitle";

import "./pages.css";

function Pages(props) {
  const [time, setTime] = useState(Date.now());
  const [sTime, setSTime] = useState(Date.now());
  const [count, setCount] = useState(0);
  const [percent, setPercent] = useState(0);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const interval = setInterval(() => setTime(Date.now()), 1000);
    setCount(count + 1);
    if(count == 3) {
      setCount(0);
    }
    return () => {
      clearInterval(interval);
    };
  }, [time]);

  useEffect(() => {
    const interval = setInterval(() => setSTime(Date.now()), 10);
    setPercent(percent + 1);
    if(percent == 100) {
      setPercent(0);
      setLoading(false);
    }
    return () => {
      clearInterval(interval);
    };
  }, [sTime]);

  useDocumentTitle(`Farq - ${props.title}`);

  return (
    <div className="container">
      <div className="loading">
        <div className="loading-bar">
          <div className="loading-1" style={{width: percent + "%"}}></div>
          <div className="loading-2"></div>
        </div>
        <div className="loading-text">Loading
        {(function (rows, i, count) {
          while (++i <= count) {
            rows.push(<span>.</span>)
          }
          return rows;
        })([], 0, count)}
        </div>
      </div>
      <div className={"bottom-1 " + (loading == false) ? "start" : "stop"}>
        <div className="bottom-black"></div>
        <div className="bottom-purple"></div>
      </div>
      <div className={"bottom-2 " + (loading == false) ? "start" : "stop"}>
        <div className="bottom-black"></div>
        <div className="bottom-purple"></div>
      </div>
    </div>
  );
}

export default Pages;
