import React from "react";

function formatPrice(price) {
  let harga = price;

  if(harga > 999999) {
    harga = harga / 1000000 + "m";
  } else if(harga > 999) {
    harga = harga / 1000 + "k";
  }

  return "Rp" + harga;
}

export default formatPrice;
