import React from "react";

import useDocumentTitle from "../useDocumentTitle";
import Header from "../components/Header";
import Sidebar from "../components/Sidebar";
import ProductCard from "../components/ProductCard";

import '../components/Variant/variant.css';

import '../components/ProductCard/productcard.css';

import "./pages.css";

function Pages(props) {
  useDocumentTitle(`Agriché - ${props.title}`);

  return (
    <div className="container">
      <Sidebar page={props.url}/>
      <div className="main">
        <Header/>
        <div className="content">
          {
            (props.allCategory === true) ?
              props.data.map(d => (
              <div className="section">
                <div className="category-title">{d.name}</div>
                <div className="underline-title"></div>
                {d.data.map(sub => (
                  <div key={sub.name} className="sub-section">
                    <div className="sub-category-title">{sub.name}</div>
                    <div className="products">
                      {sub.products.map(p => (
                        <ProductCard
                          key={p.name}
                          name={p.name}
                          image={p.image}
                          bgImage={p.bgImage}
                          price={p.price}
                          desc={p.desc}
                          hasVariants={p.hasVariants}
                          variants={p.variants}
                        />
                      ))}
                    </div>
                  </div>
                ))}
              </div>
              ))
            : <div className="section">
                <div className="category-title">{props.name}</div>
                <div className="underline-title"></div>
                {props.data.map(sub => (
                  <div key={sub.name} className="sub-section">
                    <div className="sub-category-title">{sub.name}</div>
                    <div className="products">
                      {sub.products.map(p => (
                        <ProductCard
                          key={p.name}
                          name={p.name}
                          image={p.image}
                          bgImage={p.bgImage}
                          price={p.price}
                          desc={p.desc}
                          hasVariants={p.hasVariants}
                          variants={p.variants}
                        />
                      ))}
                    </div>
                  </div>
                ))}
              </div>
          }
        </div>
      </div>
    </div>
  );
}

export default Pages;
