import React from "react";
import { Link } from "react-router-dom";

import './sidebar.css';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronDown } from "@fortawesome/free-solid-svg-icons";

import CategoryData from "../../data/categories";

var Data = JSON.stringify(CategoryData);

function Sidebar(props) {
  // console.log(Data);

  return (
    <div className="sidebar">
      <div className="side-header">
        <img src={require('../../assets/images/logo.png')} alt="profil" />
        <div className="side-title">
          <div className="title-1">Agrich√©</div>
          <div className="title-2">Joki Genshin Impact</div>
        </div>
      </div>
      <div className="side-divider"></div>
      <div className="side-nav">
        <Link to="/" className={(props.page === 'allproducts') ? 'active' : 'inactive'}>Semua Produk</Link>
        <div className="sub-nav">
          <div className="sub-nav-parent">Kategori <FontAwesomeIcon className="icon" icon={faChevronDown}/></div>
          <div className="sub-nav-items">
            {
              CategoryData.map(sub => (
                <Link to={"/kategori/" + sub.url} className={(props.page === ('kategori/' + sub.url)) ? 'active' : 'inactive'}>{sub.name}</Link>
              ))
            }
          </div>
        </div>
      </div>
    </div>
  );
}

export default Sidebar;
