import React, { useState, useEffect } from "react";
import formatPrice from "../../formatPrice";

import '../Variant/variant.css';
import { faMinus, faPlus } from "@fortawesome/free-solid-svg-icons";

import './productcard.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmark } from "@fortawesome/free-solid-svg-icons";

import { useScrollLock } from "../../useScrollLock";

function ProductCard(props) {
  const [openCard, setOpenCard] = useState();
  const { lockScroll, unlockScroll } = useScrollLock();

  const [variantQty, setVariantQty] = useState([]);
  const [subTotal, setSubTotal] = useState([]);
  const [grandTotal, setGrandTotal] = useState(0);

  function handleOpen() {
    lockScroll();
    setGrandTotal(0);
    setOpenCard(true);

    let array = []; 
    props.variants.forEach((e, i) => (array[i] = 0));
    setSubTotal(array);
    setVariantQty(array);
  }

  useEffect(() => {
    let array = []; 
    props.variants.forEach((e, i) => (array[i] = 0));
    setSubTotal(array);
    setVariantQty(array);
  }, [props.hasVariants]);


  function handleClose() {
    unlockScroll();
    setGrandTotal(0);
    setOpenCard(false);
  }

  let minPrice, maxPrice;

  if(props.hasVariants) {
    getMin();
    getMax();
  }

  function getMin() {
    minPrice = props.variants[0].price;
    props.variants.forEach(v => {
      if(v.price < minPrice) {
        minPrice = v.price;
      }
    });
  }

  function getMax() {
    maxPrice = props.variants[0].price;
    props.variants.forEach(v => {
      if(v.price > maxPrice) {
        maxPrice = v.price;
      }
    });
  }

  function Variant(props) {    
    // useEffect(() => {
    //   if (subTotal.length > 0) {
    //     setGrandTotal(calculateGrandTotal(subTotal));
    //   }
    // }, [subTotal]);

    function handleDecrement() {
      if (variantQty[props.index] > 0) {
        setVariantQty((prevQty) => {
          const newQty = prevQty.slice();
          newQty[props.index]--;
          setSubTotal((prevSubTotal) => {
            const newSubTotal = prevSubTotal.slice();
            newSubTotal[props.index] = newQty[props.index] * props.price;
            setGrandTotal(calculateGrandTotal(newSubTotal));
            return newSubTotal;
          });
          return newQty;
        });
      }
    }

    function handleIncrement() {
      if (variantQty[props.index] < props.max) {
        setVariantQty((prevQty) => {
          const newQty = prevQty.slice();
          newQty[props.index]++;
          setSubTotal((prevSubTotal) => {
            const newSubTotal = prevSubTotal.slice();
            newSubTotal[props.index] = newQty[props.index] * props.price;
            setGrandTotal(calculateGrandTotal(newSubTotal));
            return newSubTotal;
          });
          return newQty;
        });        
      }
    }

    function calculateGrandTotal(subTotalArray) {
      return subTotalArray.reduce((total, value) => total + value, 0);
    }

    return (
      <div className="variant">
        <div className={"variant-image bg-" + (props.bgImage)}>
          <img src={require("../../assets/images/" + props.image)} alt="variant-fo"/>
        </div>
        <div className="variant-info">
          <div className="variant-name">{props.name}</div>
          <div className="variant-price">{formatPrice(props.price)}</div>
        </div>
        <div className="variant-buy">
          <div className={(variantQty[props.index] > 0) ? "decrement" : "decrement disable"} onClick={handleDecrement}>
            <FontAwesomeIcon className="icon" icon={faMinus} />
          </div>
          <div>{variantQty[props.index]}</div>
          <div className={(variantQty[props.index] < props.max) ? "increment" : "increment disable"} onClick={handleIncrement}>
            <FontAwesomeIcon className="icon" icon={faPlus} />
          </div>
        </div>
      </div>
    );
  }


  return (
    <>
      <div className="product" onClick={handleOpen}>
        <div className={`image bg-` + props.bgImage}>
          <img src={require("../../assets/images/" + props.image)} alt="product-image"/>
        </div>
        <div className="info">
          <div className="name">{props.name}</div>
          <div className="price">
            {
              (props.hasVariants) ? ((minPrice === maxPrice) ? formatPrice(minPrice) : formatPrice(minPrice) + " - " + formatPrice(maxPrice)) : formatPrice(props.price)
            }
          </div>
        </div>
      </div>
      {
        (openCard) ? (
          <div className="background">
            <div className="card-container-body">
              <div className="product-card">
                <div className="product-card-body">
                  <div className={`product-card-image bg-` + props.bgImage}>
                    <img src={require("../../assets/images/" + props.image)} alt="product-image"/>
                  </div>
                  <div className="product-card-info">
                    <div className="product-card-name">{props.name}</div>
                    <div className="product-card-price">
                      {
                        (props.hasVariants) ? ((minPrice === maxPrice) ? formatPrice(minPrice) : formatPrice(minPrice) + " - " + formatPrice(maxPrice)) : formatPrice(props.price)
                      }
                    </div>
                    <div className="product-card-desc">
                      {props.desc}
                    </div>
                  </div>
                </div>
                <FontAwesomeIcon icon={faXmark} className="close-mark" onClick={handleClose} />
              </div>
              <div className="product-card-variants">
                <div className="product-card-container-variants">
                  {
                    props.variants.map((v, i) => (
                      <Variant key={i} index={i} name={v.name} price={v.price} image={v.image} bgImage={v.bgImage} max={v.max} />
                    ))
                  }
                </div>
              </div>
            </div>
            <div className="product-card-footer">
                <div className="product-card-total">
                  Total : <span>{formatPrice(grandTotal)}</span>
                </div>
                <button>Simpan</button>
              </div>
          </div>
        ) : <></>
      }
    </>
  );
}


export default ProductCard;
